package com.mindtree.franchise.dto;

public class TeamDto {

	private int id;
	private String name;
	private byte limit;

	public TeamDto(int id, String name, byte limit) {
		super();
		this.id = id;
		this.name = name;
		this.limit = limit;
	}

	public TeamDto() {
		super();
		// TODO Auto-generated constructor stub
	}

	public byte getLimit() {
		return limit;
	}

	public void setLimit(byte limit) {
		this.limit = limit;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	@Override
	public String toString() {
		return "Team [id=" + id + ", name=" + name + ", limit=" + limit + "]";
	}

}
